INSERT INTO MOVIE (STAR_RATING, TITLE, YEAR_OF_RELEASE, URI) VALUES (7.8, 'Sound of Metal', 2019, 'https://www.primevideo.com/detail/0GI1AFJL8QBNE2EZ0M30EOZO0Z/ref=atv_dp_share_cu_r')
INSERT INTO REVIEW (STAR, REVIEW_TITLE, REVIEW_AUTHOR, REVIEW_CONTENT, TIME) VALUES (9, 'Powerful and thought provoking', 'Surecure', 'Sound of Metal is one of those films which will keep you thinking for days after about so many aspects of life such as addiction and how some relationships may simply serve a purpose in the short term for our life but ultimately must be let go.', '2019-09-09 00:00:00')
INSERT INTO REVIEW (STAR, REVIEW_TITLE, REVIEW_AUTHOR, REVIEW_CONTENT, TIME) VALUES (10, 'Masterpiece!', 'cansarica-34870', 'Whiplash is a story of "If you work hard, you succeed." This one is "If you work hard, you succeed, then one day you can lose everything in a second, and if you work harder, you can realize that "everything" is actually nothing."', '2020-12-12 00:00:00')
INSERT INTO REVIEW (STAR, REVIEW_TITLE, REVIEW_AUTHOR, REVIEW_CONTENT, TIME) VALUES (8, 'Not perfect, but real enough', 'digitalbeachbum', 'The Sound of Metal is a good play on words, because the main character has to deal with the loss of his "metal" music, but later that word comes to mean something else which you will need to watch the movie to discover.', '2020-12-10 00:00:00')
INSERT INTO MOVIE (STAR_RATING, TITLE, YEAR_OF_RELEASE, URI) VALUES (8.4, 'Joker', 2019, 'https://play.google.com/store/movies/details?id=rj06VI6rv58.P')
INSERT INTO REVIEW (STAR, REVIEW_TITLE, REVIEW_AUTHOR, REVIEW_CONTENT, TIME) VALUES (10, 'Outstanding movie with a haunting performance and best character development ever seen', 'MihaVrhunc', 'Every once in a while a movie comes, that truly makes an impact. Joaquin''s performance and scenography in all it''s brilliance. Grotesque, haunting and cringy. Hard to watch at times,...', '2019-10-03 00:00:00')
INSERT INTO REVIEW (STAR, REVIEW_TITLE, REVIEW_AUTHOR, REVIEW_CONTENT, TIME) VALUES (10, 'Only certain people can relate', 'lesterarnoldpinto', 'This is a movie that only those who have felt alone and isolated can truly relate to it. You understand the motive and you feel sorry for the character. A lot of people will see this movie and think that it encourages violence.', '2019-10-07 00:00:00')
INSERT INTO REVIEW (STAR, REVIEW_TITLE, REVIEW_AUTHOR, REVIEW_CONTENT, TIME) VALUES (10, 'Perfect in every aspect.', 'Aman_Goyal', 'Truly a masterpiece, The Best Hollywood film of 2019, one of the Best films of the decade... And truly the Best film to bring a comic book so chillingly and realistically to real ife. Remarkable Direction, Cinematography, Music and the Acting.', '2019-09-01 00:00:00')
